import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.concurrent.TimeUnit;

/**
 * @author Maksym Shevchuk
 */
public class MainServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp)
            throws ServletException, IOException {
        PrintWriter out = resp.getWriter();
        try {
            out.print("<html>");
            out.print("<body>");
            out.print("<div style=\"text-align: center;\">");
            out.print("<br/><br/><br/><br/>");
            out.print("<img src=\"data:image/png;base64,\" />");
            out.print("<h1>Live Migration Checker</h1>");
            out.print("<br/><br/><br/>");
            out.print("<span><b><font color = \"blue\" size = 14>Uptime: " + getSystemUptime() + "</font></b></span>");
            out.print("<div/>");
            out.print("<body/>");
            out.print("<html/>");

        } catch (Exception e) {
            out.print("<h1>Error occurred during getSystemUptime()</h1>");
        }
    }

    public static String getSystemUptime() throws Exception {
        BufferedReader in = new BufferedReader(new InputStreamReader(Runtime.getRuntime().exec("cat /proc/uptime").getInputStream()));
        String uptime = in.readLine();

        return getDurationString(Integer.parseInt(uptime.substring(0, uptime.indexOf("."))));
    }

    public static String getDurationString(int seconds) {
        int[] duration = getDuration(seconds);

        if (duration[0] == 0) {
            return String.format("%02dh %02dm %02ds", duration[1], duration[2], duration[3]);
        } else {
            return String.format("%dd %02dh %02dm", duration[0], duration[1], duration[2]);
        }
    }

    public static int[] getDuration(int seconds) {
        int[] duration = new int[4];
        duration[0] = (int) TimeUnit.DAYS.convert(seconds, TimeUnit.SECONDS);
        duration[1] = (int) TimeUnit.HOURS.convert(seconds, TimeUnit.SECONDS) % 24;
        duration[2] = (int) TimeUnit.MINUTES.convert(seconds, TimeUnit.SECONDS) % 60;
        duration[3] = seconds % 60;

        return duration;
    }
}
